import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { taskNotificationScheduler } from "./notificationScheduler";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  // Environment validation and startup logging
  log("Starting FlowHub Command Center...");
  log(`Database: ${process.env.DATABASE_URL ? 'PostgreSQL' : 'Memory (development mode)'}`);
  log(`Gemini API: ${process.env.GEMINI_API_KEY ? 'configured' : 'not configured'}`);
  log(`Google OAuth: ${process.env.GOOGLE_CLIENT_ID && process.env.GOOGLE_CLIENT_SECRET ? 'configured' : 'not configured'}`);

  const server = await registerRoutes(app);

  // Start the task notification scheduler
  taskNotificationScheduler.start();

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
    log(`Task notification scheduler started`);
  });
})();

server/notificationScheduler.ts.content:
import { storage } from "./storage";
import type { Task } from "../shared/schema";

interface TaskReminder {
  taskId: string;
  reminderTime: Date;
  reminderType: string;
  sent: boolean;
}

class TaskNotificationScheduler {
  private reminders: Map<string, TaskReminder[]> = new Map();
  private interval: NodeJS.Timeout | null = null;

  start() {
    // Check for reminders every minute
    this.interval = setInterval(() => {
      this.checkAndSendReminders();
    }, 60000);

    // Backfill reminders for existing tasks on startup
    this.backfillExistingTaskReminders();
  }

  stop() {
    if (this.interval) {
      clearInterval(this.interval);
      this.interval = null;
    }
  }

  async scheduleTaskReminders(task: Task) {
    if (!task.dueAt || task.status === 'completed') return;

    const dueDate = new Date(task.dueAt);
    const now = new Date();
    const reminders: TaskReminder[] = [];

    // Schedule reminders: 1 hour, 30 min, 15 min, 10 min, 5 min before due
    const reminderTimes = [
      { minutes: 60, label: "1 hour" },
      { minutes: 30, label: "30 minutes" },
      { minutes: 15, label: "15 minutes" },
      { minutes: 10, label: "10 minutes" },
      { minutes: 5, label: "5 minutes" }
    ];

    for (const reminder of reminderTimes) {
      const reminderTime = new Date(dueDate.getTime() - reminder.minutes * 60 * 1000);

      // Only schedule if reminder time is in the future
      if (reminderTime > now) {
        reminders.push({
          taskId: task.id,
          reminderTime,
          reminderType: reminder.label,
          sent: false
        });
      }
    }

    if (reminders.length > 0) {
      this.reminders.set(task.id, reminders);
    }
  }

  private async checkAndSendReminders() {
    const now = new Date();

    // Check for priority promotions (Important -> Urgent when < 2 hours)
    await this.checkTaskPriorityPromotions();

    for (const [taskId, taskReminders] of this.reminders.entries()) {
      for (const reminder of taskReminders) {
        if (!reminder.sent && reminder.reminderTime <= now) {
          await this.sendTaskReminder(taskId, reminder.reminderType);
          reminder.sent = true;
        }
      }

      // Clean up sent reminders
      const activeReminders = taskReminders.filter(r => !r.sent);
      if (activeReminders.length === 0) {
        this.reminders.delete(taskId);
      } else {
        this.reminders.set(taskId, activeReminders);
      }
    }
  }

  private async checkTaskPriorityPromotions() {
    try {
      console.log('[TaskScheduler] Checking for priority promotions...');

      // Get all important tasks across all users that are not completed
      const importantTasks = await storage.getTasksByPriority("important");
      console.log(`[TaskScheduler] Found ${importantTasks.length} important tasks`);

      const activeTasks = importantTasks.filter(task => 
        task.status !== "completed" && 
        task.dueAt
      );
      console.log(`[TaskScheduler] Found ${activeTasks.length} active tasks with due dates`);

      const now = new Date();
      for (const task of activeTasks) {
        const dueDate = new Date(task.dueAt!);
        const timeRemaining = dueDate.getTime() - now.getTime();
        const hoursRemaining = timeRemaining / (1000 * 60 * 60);

        // Promote to urgent if less than 2 hours remaining
        if (hoursRemaining < 2 && hoursRemaining > 0) {
          await storage.updateTask(task.id, { priority: "urgent" });
          console.log(`[TaskScheduler] Promoted task "${task.title}" from Important to Urgent (${Math.round(hoursRemaining * 60)} minutes remaining)`);
        }
      }
    } catch (error) {
      console.error("Error checking task priority promotions:", error);
    }
  }

  private async sendTaskReminder(taskId: string, reminderType: string) {
    try {
      const task = await storage.getTaskById(taskId);
      if (!task || task.status === 'completed') return;

      let sourceType = "Manual";
      if (task.metadata?.aiGenerated) {
        sourceType = "AI Converted";
      } else if (task.sourceApp === "gmail") {
        sourceType = "Mail Converted";
      }

      // Store notification data for browser notification API
      await storage.createNotification({
        userId: task.userId,
        title: `Task Due in ${reminderType}`,
        description: `${sourceType} task "${task.title}" is due in ${reminderType}. ${task.description || ''}`,
        type: "browser_notification",
        sourceApp: "system",
        aiSummary: `Reminder for ${sourceType.toLowerCase()} task due in ${reminderType}`,
        actionableInsights: ["Complete task", "Reschedule task", "Mark as done"],
        metadata: {
          taskId: task.id,
          reminderType,
          sourceType,
          browserNotification: true
        }
      });

      console.log(`[TaskScheduler] Created browser notification for task "${task.title}" due in ${reminderType}`);

    } catch (error) {
      console.error(`Failed to send reminder for task ${taskId}:`, error);
    }
  }

  removeTaskReminders(taskId: string) {
    this.reminders.delete(taskId);
  }

  private async backfillExistingTaskReminders() {
    try {
      console.log('[TaskScheduler] Backfilling reminders for existing tasks...');

      // Get all tasks with future due dates
      const allTasks = await storage.getTasksByPriority("urgent");
      const importantTasks = await storage.getTasksByPriority("important");
      const normalTasks = await storage.getTasksByPriority("normal");

      const tasksWithDueDates = [...allTasks, ...importantTasks, ...normalTasks]
        .filter(task => task.dueAt && task.status !== 'completed' && new Date(task.dueAt) > new Date());

      for (const task of tasksWithDueDates) {
        await this.scheduleTaskReminders(task);
      }

      console.log(`[TaskScheduler] Backfilled reminders for ${tasksWithDueDates.length} tasks`);
    } catch (error) {
      console.error('Error backfilling task reminders:', error);
    }
  }
}

export const taskNotificationScheduler = new TaskNotificationScheduler();

server/openai.ts.content:
// Remove unused fs import
import { GoogleGenAI, Modality } from "@google/genai";

// This API key is from Gemini Developer API Key, not vertex AI API Key
let ai: GoogleGenAI | null = null;

if (process.env.GEMINI_API_KEY) {
  ai = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY });
}

export interface NotificationAnalysis {
  priority: "urgent" | "important" | "informational";
  summary: string;
  actionableInsights: string[];
  estimatedTimeToHandle: number;
  suggestedActions: string[];
}

export interface WorkflowOptimization {
  suggestions: {
    type: "reorder" | "batch" | "delegate" | "postpone";
    description: string;
    estimatedTimeSaving: number;
    confidence: number;
  }[];
  optimizedSchedule: {
    taskId: string;
    suggestedStartTime: string;
    reason: string;
  }[];
}

// Note that the newest Gemini model series is "gemini-2.5-flash" or gemini-2.5-pro"
export async function analyzeNotification(
  title: string,
  content: string,
  sourceApp: string
): Promise<NotificationAnalysis> {
  // If AI is not available, return basic analysis
  if (!ai) {
    const priority = content.toLowerCase().includes("urgent") || title.toLowerCase().includes("urgent") 
      ? "urgent" : "important";
    return {
      priority: priority as any,
      summary: `${sourceApp}: ${title}`,
      actionableInsights: ["Review notification", "Take appropriate action"],
      estimatedTimeToHandle: 10,
      suggestedActions: ["Read content", "Respond if needed"]
    };
  }

  try {
    const systemPrompt = `You are an executive assistant AI analyzing workplace notifications for a corporate command center. 
    Analyze the notification and provide priority classification, summary, and actionable insights.
    Consider business context, urgency indicators, and deadline implications.
    Respond with JSON in this exact format: {
      "priority": "urgent|important|informational",
      "summary": "concise summary in 1-2 sentences",
      "actionableInsights": ["insight1", "insight2"],
      "estimatedTimeToHandle": number_in_minutes,
      "suggestedActions": ["action1", "action2"]
    }`;

    const response = await ai.models.generateContent({
      model: "gemini-2.5-flash",
      config: {
        systemInstruction: systemPrompt,
        responseMimeType: "application/json",
        responseSchema: {
          type: "object",
          properties: {
            priority: { type: "string" },
            summary: { type: "string" },
            actionableInsights: { type: "array", items: { type: "string" } },
            estimatedTimeToHandle: { type: "number" },
            suggestedActions: { type: "array", items: { type: "string" } },
          },
          required: ["priority", "summary", "actionableInsights", "estimatedTimeToHandle", "suggestedActions"],
        },
      },
      contents: `Source App: ${sourceApp}\nTitle: ${title}\nContent: ${content}`,
    });

    const rawJson = response.text;
    if (rawJson) {
      const result = JSON.parse(rawJson);
      return {
        priority: result.priority || "informational",
        summary: result.summary || "No summary available",
        actionableInsights: result.actionableInsights || [],
        estimatedTimeToHandle: result.estimatedTimeToHandle || 5,
        suggestedActions: result.suggestedActions || [],
      };
    } else {
      throw new Error("Empty response from model");
    }
