@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&family=Playfair+Display:wght@400;500;600;700&family=JetBrains+Mono:wght@300;400;500;600;700&family=Sohne:wght@400;500;600;700&family=GT+America:wght@400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;


/* Enhanced Light Theme Styles */
:root.light {
  /* Glass effect for light theme */
  --glass-bg: rgba(255, 255, 255, 0.85);
  --glass-border: rgba(0, 0, 0, 0.15);
  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);

  /* Enhanced gradient backgrounds */
  --gradient-primary: linear-gradient(135deg, hsl(221, 83%, 53%) 0%, hsl(221, 83%, 60%) 100%);
  --gradient-secondary: linear-gradient(135deg, hsl(210, 40%, 94%) 0%, hsl(210, 40%, 98%) 100%);

  /* Button hover effects */
  --hover-overlay: rgba(0, 0, 0, 0.05);
}

:root.dark {
  /* Glass effect for dark theme */
  --glass-bg: rgba(0, 0, 0, 0.3);
  --glass-border: rgba(255, 255, 255, 0.1);
  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);

  /* Enhanced gradient backgrounds */
  --gradient-primary: linear-gradient(135deg, hsl(212, 100%, 47%) 0%, hsl(212, 100%, 54%) 100%);
  --gradient-secondary: linear-gradient(135deg, hsl(217, 32%, 17%) 0%, hsl(217, 32%, 20%) 100%);

  /* Button hover effects */
  --hover-overlay: rgba(255, 255, 255, 0.05);
}

/* Enhanced Glass Card Effect */
.glass-card {
  background: var(--glass-bg);
  backdrop-filter: blur(20px) saturate(150%);
  border: 1px solid var(--glass-border);
  box-shadow: var(--glass-shadow);
}

/* Enhanced Button Styles for Light Theme */
.light .glass-effect,
.light .glass-card {
  background: var(--glass-bg);
  backdrop-filter: blur(16px) saturate(120%);
  border: 1px solid rgba(0, 0, 0, 0.12);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
}

.light .glass-effect:hover,
.light .glass-card:hover {
  background: rgba(255, 255, 255, 0.95);
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.12);
  transform: translateY(-1px);
  transition: all 0.2s ease;
}

/* Light Theme Icon Colors */
.light svg {
  color: rgba(0, 0, 0, 0.8) !important;
}

.light .text-primary svg,
.light .text-blue-500,
.light .text-red-500,
.light .text-purple-500,
.light .text-green-500,
.light .text-green-600 {
  color: inherit !important;
}

/* Profile Avatar Enhancement */
.light .premium-logo-glow {
  filter: drop-shadow(0 2px 8px rgba(0, 0, 0, 0.1));
}

/* Enhanced Cards for Light Theme */
.light [data-testid="card-notifications"],
.light [data-testid="card-tasks"],
.light [data-testid="card-app-launcher"] {
  background: rgba(255, 255, 255, 0.95);
  border: 1px solid rgba(0, 0, 0, 0.12);
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
}

/* Better contrast for text in light mode */
.light .text-muted-foreground {
  color: hsl(215, 16%, 38%) !important;
}

/* Enhanced navigation buttons */
.light button[variant="ghost"] {
  background: rgba(255, 255, 255, 0.6);
  border: 1px solid rgba(0, 0, 0, 0.05);
}

.light button[variant="ghost"]:hover {
  background: rgba(255, 255, 255, 0.9);
  border: 1px solid rgba(0, 0, 0, 0.1);
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

/* Global Styles */
body {
  margin: 0;
  padding: 0;
  font-family: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', 'Open Sans', 'Helvetica Neue', sans-serif;
  font-display: swap;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  overflow-x: hidden;
}

/* Ensure all pages are scrollable */
html, body {
  height: 100%;
  overflow-y: auto;
}

#root {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

/* Page scroll fixes */
.min-h-screen {
  min-height: 100vh;
  max-height: 100vh;
  overflow-y: auto;
}

/* Main content scroll fixes */
main {
  overflow-y: auto !important;
  flex: 1;
  min-height: 0;
}

/* Prevent duplicate content and fix scrolling */
/* The previous rules for main, dashboard-container, and root are now handled by the new global styles.
   Keeping these commented out for reference but they are effectively superseded. */
/*
main {
  min-height: 100vh;
  position: relative;
  overflow-y: auto;
  padding-top: 80px;
}

.dashboard-container {
  min-height: 100vh;
  position: relative;
  overflow: hidden;
}

#root {
  min-height: 100vh;
  position: relative;
  overflow: hidden;
}
*/

/* Mobile Responsive Layout */
@media (max-width: 768px) {
  /* Hide app launcher on mobile */
  [data-testid="card-app-launcher"] {
    display: none !important;
  }

  /* Mobile dashboard layout adjustments */
  .dashboard-grid {
    grid-template-columns: 1fr !important;
    gap: 1rem !important;
  }

  /* Desktop dashboard layout - allow sections to grow naturally */
  .dashboard-grid {
    display: flex;
    align-items: start;
    gap: 1.5rem;
    min-height: calc(100vh - 120px);
  }

  /* Ensure mobile content fits properly */
  main {
    min-height: auto;
