  }

  /* Stack tasks and notifications vertically on mobile */
  .mobile-task-notification-stack {
    display: flex;
    flex-direction: column;
    gap: 1rem;
  }

  /* Adjust button spacing for mobile - force horizontal layout */
  .mobile-button-group {
    display: flex !important;
    flex-direction: row !important;
    flex-wrap: nowrap !important;
    gap: 0.5rem;
    justify-content: flex-start;
    align-items: center;
  }

  .mobile-button-group .button {
    flex-shrink: 0;
    min-width: 80px;
  }

  /* Force task section buttons to display horizontally */
  [data-testid="card-tasks"] .space-y-3,
  [data-testid="card-tasks"] .flex-col {
    display: flex !important;
    flex-direction: row !important;
    gap: 8px !important;
    align-items: center !important;
  }

  /* Ensure task buttons container stays horizontal */
  .tasks-header-buttons {
    display: flex !important;
    flex-direction: row !important;
    gap: 8px !important;
    align-items: center !important;
    flex-wrap: nowrap !important;
  }

  /* Force horizontal layout for task buttons - strongest possible selectors */
  @media (max-width: 768px) {
    /* Target the specific task buttons container */
    [data-testid="card-tasks"] div[class*="space-y"],
    [data-testid="card-tasks"] div[class*="flex-col"],
    [data-testid="card-tasks"] .space-y-3,
    [data-testid="card-tasks"] .flex-col {
      display: flex !important;
      flex-direction: row !important;
      gap: 8px !important;
      align-items: center !important;
      justify-content: flex-start !important;
      flex-wrap: nowrap !important;
    }

    /* Target any parent containers that might be forcing column layout */
    [data-testid="card-tasks"] > div > div,
    [data-testid="card-tasks"] [role="group"],
    [data-testid="card-tasks"] .tasks-header,
    [data-testid="card-tasks"] .task-buttons-container {
      display: flex !important;
      flex-direction: row !important;
      gap: 8px !important;
      align-items: center !important;
      flex-wrap: nowrap !important;
    }

    /* Override any Tailwind classes that might be forcing vertical layout */
    [data-testid="card-tasks"] * {
      flex-direction: row !important;
    }

    [data-testid="card-tasks"] .space-y-1,
    [data-testid="card-tasks"] .space-y-2,
    [data-testid="card-tasks"] .space-y-3,
    [data-testid="card-tasks"] .space-y-4 {
      display: flex !important;
      flex-direction: row !important;
      gap: 8px !important;
      margin-top: 0 !important;
      margin-bottom: 0 !important;
    }

    [data-testid="card-tasks"] .space-y-1 > *,
    [data-testid="card-tasks"] .space-y-2 > *,
    [data-testid="card-tasks"] .space-y-3 > *,
    [data-testid="card-tasks"] .space-y-4 > * {
      margin-top: 0 !important;
      margin-bottom: 0 !important;
    }
  }

  /* Mobile header optimizations */
  [data-testid="header-main"] .container {
    padding-left: 1rem !important;
    padding-right: 1rem !important;
  }

  /* Compact mobile buttons */
  @media (max-width: 640px) {
    .mobile-compact-btn {
      padding: 0.25rem !important;
      min-width: auto !important;
    }

    .mobile-compact-btn span {
      display: none !important;
    }
  }

  /* Task section button improvements for mobile */
  @media (max-width: 768px) {
    [data-testid="button-add-ai-task"],
    [data-testid="button-add-manual-task-plus"],
    [data-testid="button-auto-reschedule"] {
      height: 36px !important;
      min-height: 36px !important;
      max-height: 36px !important;
      font-size: 14px !important;
      white-space: nowrap;
      flex-shrink: 0;
      border-radius: 8px !important;
    }

    /* AI task button - minimal width with "AI" text and icon */
    [data-testid="button-add-ai-task"] {
      min-width: 50px !important;
      width: auto !important;
      padding: 0 8px !important;
    }

    /* Manual task button - minimal width with just plus icon */
    [data-testid="button-add-manual-task-plus"] {
      min-width: 36px !important;
      width: 36px !important;
      padding: 0 !important;
    }

    /* Auto reschedule button */
    [data-testid="button-auto-reschedule"] {
      min-width: 36px !important;
      width: 36px !important;
      padding: 0 !important;
    }
  }

  /* Extra small screens (iPhone SE and similar) */
  @media (max-width: 375px) {
    [data-testid="button-add-ai-task"],
    [data-testid="button-add-manual-task-plus"],
    [data-testid="button-auto-reschedule"] {
      height: 34px !important;
      min-height: 34px !important;
      max-height: 34px !important;
      font-size: 13px !important;
    }

    [data-testid="button-auto-reschedule"] {
      min-width: 34px !important;
      width: 34px !important;
    }

    [data-testid="button-add-ai-task"],
    [data-testid="button-add-manual-task-plus"] {
      min-width: 75px !important;
      padding: 0 10px !important;
    }
  }

  /* Upgrade modal mobile optimization */
  .upgrade-modal-content {
    margin: 0.5rem !important;
    max-height: calc(100vh - 1rem) !important;
    width: calc(100vw - 1rem) !important;
    overflow-y: auto !important;
    -webkit-overflow-scrolling: touch !important;
    position: fixed !important;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%) !important;
    z-index: 9999 !important;
  }

  /* Ensure modal content is scrollable on very small screens */
  @media (max-height: 600px) {
    .upgrade-modal-content {
      max-height: 95vh !important;
      overflow-y: scroll !important;
    }
  }

  /* Fix modal body scrolling */
  .upgrade-modal-content > * {
    flex-shrink: 0;
  }
}


@tailwind utilities;

@keyframes shimmer {
  0% {
    transform: translateX(-100%);
  }
  100% {
    transform: translateX(100%);
  }
}

@keyframes shimmerInner {
  0% {
    transform: translateX(-100%) scale(0.8);
    opacity: 0;
  }
  50% {
    opacity: 1;
    transform: translateX(0%) scale(1);
  }
  100% {
    transform: translateX(100%) scale(0.8);
    opacity: 0;
  }
}

@keyframes shimmerOuter {
  0% {
    transform: translateX(-150%);
    opacity: 0;
  }
  50% {
    opacity: 1;
  }
  100% {
    transform: translateX(150%);
    opacity: 0;
  }
}

.shimmer {
  position: relative;
  overflow: hidden;
}

.shimmer::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
  animation: shimmer 2s infinite;
}

.animate-shimmer {
  animation: shimmer 3s linear infinite;
}

.animate-shimmer-inner {
  animation: shimmerInner 3s ease-in-out infinite;
}

.animate-shimmer-outer {
  animation: shimmerOuter 4s ease-in-out infinite 1s;
}

