    refetchIntervalInBackground: true,
  });
}

export function useCreateTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (task: InsertTask) => {
      return await apiRequest("POST", "/api/tasks", task);
    },
    onSuccess: () => {
