    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tasks", { userId: MOCK_USER_ID }] });
    },
  });
}

export function useStopTask() {
  const queryClient = useQueryClient();

  return useMutation({
    mutationFn: async (taskId: string) => {
