  const userInitials = getDisplayName()
    .split(' ')
    .map((name: string) => name[0]?.toUpperCase())
    .join('')
    .slice(0, 2);

  // Get the current profile image URL with proper cache busting
  const getCurrentProfileImage = () => {
    // During editing, prioritize edit data
