              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Hidden image element for processing */}
      {originalImage && (
        <img
          ref={imageRef}
          src={originalImage}
          alt="Original"
          className="hidden"
          onLoad={drawCanvas}
        />
      )}
    </>
  );
}

